unsigned int ir_data[25] = {0x00FF906F, 0x00FFC03F, 0x00FF38C7, 0x00FFA05F, 0x00FF708F, 0x00FF58A7, 0x00FF50AF, 0x00FF7887, 0x00FF40BF, 0x00FF30CF, 0x00FF2AD5, 0x00FF32CD, 0x00FF3AC5, 0x00FF8A75, 0x00FFB04F, 0x00FF9867, 0x00FFD827, 0x00FF807F, 0x00FF8877, 0x00FFA857, 0x00FFE817, 0x00FF609F, 0x00FF48B7, 0x00FF6897, 0xFFFFFFFF};

String ir_value[25] = {"Power On/Off", "Mute", "Menu", "Ok", "Up", "Down", "Left", "Right", "Volume Up", "Volume Down", "Pause", "Signal", "View", "Audio", "1", "2", "3", "4", "5", "6", "7", "8", "9", "0", " "};

IRsend irsend(ir);

String data;

void setup() {
  WiFi.mode(WIFI_AP);
  WiFi.softAP(ssid, password);
  WiFi.softAPConfig(ip, ip, mask);
  irsend.begin();

  s.on("/", HTTP_GET, [](AsyncWebServerRequest *req){
    req->send_P(200, "text/html", content);
  });

  s.on("/click", HTTP_POST, [](AsyncWebServerRequest *req){
    if (req->hasParam("data", true)){
        data = req->getParam("data", true)->value();
        for (int i = 0; i < 25; i++){
          if (data == ir_value[i]){
            irsend.sendNEC(ir_data[i], 32);
            break;
          }
        }
        
        req->send_P(200, "text/plain", "Received!");
    } else {
      req->send_P(400, "text/plain", "ERROR");
    }
  });

  s.begin();
  
}

void loop() {
  
}